{
    "head": {
        "title": "Another Zucchini Dish",
        "categories": [
            "Microwave",
            "Vegetables"
        ],
        "yield": 6
    },
    "ingredients": [
        {
            "amt": {
                "qty": 1,
                "unit": "pound"
            },
            "item": "Zucchini; cubed 1/2 \""
        },
        {
            "amt": {
                "qty": 3,
                "unit": "tablespoons"
            },
            "item": "Butter or margarine"
        },
        {
            "amt": {
                "qty": 3,
                "unit": ""
            },
            "item": "Eggs; beaten"
        },
        {
            "amt": {
                "qty": 1,
                "unit": ""
            },
            "item": "Jar pimentos; 2 1/2 oz, diced"
        },
        {
            "amt": {
                "qty": 1,
                "unit": "cup"
            },
            "item": "Cheddar cheese; shredded"
        },
        {
            "amt": {
                "qty": 1,
                "unit": "can"
            },
            "item": "French fried onion rings 3 oz."
        }
    ],
    "directions": {
        "step": "Place zucchini and butter in a 2 quart casserole. Cover with plastic wrap.\n                Microwave at high (100%) until tender crisp, 4 to 5 minutes. Stir in egg,\n                pimentos and cheese. Blend well. Cover with plastic wrap. Microwave at\n                medium (50%) until eggs are set, 10 toll minutes. Sprinkle onion rings on\n                top. Microwave at medium (50%) until onion rings are heated, 2 to 2 1/2\n                minutes. Makes 6 servings.\n\n                Recipe by: diane@keyway.net\n\n                Posted to recipelu-digest Volume 01 Number 217 by \"Diane Geary\"\n                &lt;diane@keyway.net&gt; on Nov 7, 1997"
    }
}
